query($callId:Int!) {
blankProposal(callId:$callId) {
  id
  status
  shortCode
  proposer {
    id
    firstname
    lastname
    organisation
  }
  questionary {
    steps {
      topic {
        title
        id
      }
      isCompleted
      fields {
          question {
            proposalQuestionId
            dataType
            question
            config
            {
              ... on BooleanConfig {
                required
              }
            }
          }

        value
        dependency {
          questionId
            dependencyId
            dependencyNaturalKey
            condition {
                  condition
                  params
            }
        }
      }
    }
  }
}
}

mutation ($callId: Int!) {
  createProposal(callId: $callId) {
    proposal {
      id
      status
      questionary {
        steps {
          topic {
            title
            id
          }
          fields {
            question {
              proposalQuestionId
              dataType
              question
              config {
                ... on BooleanConfig {
                  small_label
                  required
                  tooltip
                }
                ... on DateConfig {
                  small_label
                  required
                  tooltip
                }
                ... on EmbellishmentConfig {
                  html
                  plain
                  small_label
                  required
                  tooltip
                  omitFromPdf
                }
                ... on FileUploadConfig {
                  file_type
                  max_files
                  small_label
                  required
                  tooltip
                }
                ... on SelectionFromOptionsConfig {
                  variant
                  options
                  small_label
                  required
                  tooltip
                }
                ... on TextInputConfig {
                  min
                  max
                  multiline
                  placeholder
                  small_label
                  required
                  tooltip
                }
              }
            }
            value
            dependency {
              questionId
              dependencyId
              dependencyNaturalKey
              condition {
                condition
                params
              }
            }
          }
        }
      }
    }
    error
  }
}

